import React, {useState} from "react";
import VisibilityIcon from '@mui/icons-material/Visibility';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import {NavLink} from "react-router-dom";

function Home() {

    const [name, setName] = useState();
    const [email, setEmail] = useState();
    const [job, setJob] = useState();
    const [phone, setPhone] = useState();
    const [address, setAddress] = useState();

    const getData = async(e) =>{
        e.preventDefault();

        const val = {name, email, job, phone, address};
        console.log(val);

        const res = await fetch("/add", {
            method: "POST",
            headers: {"Content-Type":"application/json"},
            body: JSON.stringify(val)
        });

        const data = await res.json();
        console.log(data);

        if(res.status === 404 || !data){
            alert("Data not inserted!");
            console.log("Data not inserted!");
        }else{
            alert("Data Inserted!");
            console.log("Data Inserted!");
        }
    }

    return (
        <div className="mt-5">
            <div className="container">
                <div className="add_btn mt-2">
                    <NavLink to="/add" className="btn btn-primary">Add Data</NavLink>
                </div>

                <table className="table mt-2">
                    <thead>
                        <tr className="table-dark">
                            <th scope="col">id</th>
                            <th scope="col">Username</th>
                            <th scope="col">Email</th>
                            <th scope="col">Job</th>
                            <th scope="col">Number</th>
                            <th scope="col">Address</th>
                            <th scope="col"></th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                            <th scope="row">1</th>
                            <td>Karishma</td>
                            <td>karishma@gmail.com</td>
                            <td>Front-end developer</td>
                            <td>+923144678123</td>
                            <td>Defence Phase-2</td>
                            <td className="d-flex justify-content-between">
                                <button className="btn btn-success"><VisibilityIcon /></button>
                                <button className="btn btn-primary"><EditIcon /></button>
                                <button className="btn btn-danger"><DeleteIcon /></button>
                            </td>
                        </tr>
                        {/* <tr>
                            <th scope="row">2</th>
                            <td>Sadia</td>
                            <td>sadia@gmail.com</td>
                            <td>MERN Stack Developer</td>
                            <td>+923223123456</td>
